mapper.select:
SELECT po.PRODUCT_ORDER_ID AS 'productOrderId', pc.NAME AS 'productCompanyName', s.NAME AS 'sourceName', sp.PRICE AS 'sourcePrice', po.QUENTITY AS 'quantity', (po.QUENTITY * sp.PRICE) AS 'totalPrice', po.PRODUCT_ORDER_DATE as 'productOrderDate', st.STATUS as 'productOrderStatus' FROM PRODUCT_ORDER po JOIN KIT_SOURCE_PRICE ksr ON ksr.KIT_SOURCE_PRICE_ID = po.KIT_SOURCE_PRICE_ID JOIN SOURCE_PRICE sp ON sp.SOURCE_PRICE_ID = ksr.SOURCE_PRICE_ID JOIN SOURCE s ON s.SOURCE_ID = sp.SOURCE_ID JOIN PRODUCT_COMPANY pc ON pc.PRODUCT_COMPANY_ID = sp.PRODUCT_COMPANY_ID JOIN STATUS st ON st.STATUS_ID = po.STATUS_ID WHERE 1 = 1
AND pc.PRODUCT_COMPANY_ID = #{productCompanyId}
AND st.STATUS_ID = #{statusId}
